function drawCircleCircum(){
ctx.beginPath();
ctx.arc(95, 50, 40, 0, 2 * Math.PI);
ctx.stroke();
}

function drawRect(x,y,width,height,color){

ctx.fillStyle = color;
ctx.fillRect(x,y, width,height);


}

function drawRectPerimeter(x,y,width,height,color){

  ctx.beginPath();
  ctx.fillStyle = color;
  ctx.rect(x, x, width,height);
  ctx.stroke();
}


function drawCircle(x,y, radius,color){

ctx.fillStyle = color;
ctx.beginPath();
ctx.arc(x, y, radius, 0, 2 * Math.PI);
ctx.fill();

}



function drawText(text, x,y, fSizeInt, fColor){

ctx.fillStyle = fColor;
	ctx.font = ""+fSizeInt+"px serif";
  ctx.fillText(text, x, y);


}